import React, { useEffect, useRef, useState } from 'react';
import section1Image from '../assets/imgs/carrousel_img_1.jpg';
import section2Image from '../assets/imgs/carrousel_img_2.jpg';
import section3Image from '../assets/imgs/carrousel_img_3.jpg';

const sectionImages = [section1Image, section2Image, section3Image];
const NUM_STRIPS = 20;
const ANIMATION_DURATION = 500;
const STRIP_DELAY = 30;
const EFFECT_RADIUS = 3;

const HomeCarousel = ({ section }) => {
  const canvasRef = useRef(null);
  const [image, setImage] = useState(null);
  const [hoveredStrip, setHoveredStrip] = useState(null);
  const animationRef = useRef(null);
  const stripsProgress = useRef(Array(NUM_STRIPS).fill(0));
  const effectIntensity = useRef(Array(NUM_STRIPS).fill(0));
  const isAnimating = useRef(false);
  const currentStrip = useRef(null);
  const offscreenCanvas = useRef(document.createElement('canvas'));

  // Cargar imagen actual
  useEffect(() => {
    const img = new Image();
    img.src = sectionImages[section];
    img.onload = () => {
      setImage(img);
      drawInitialState();
    };
    img.onerror = (e) => console.error('Error loading image:', e);
  }, [section]);

  const drawInitialState = () => {
    const ctx = canvasRef.current?.getContext('2d');
    if (!ctx) return;
    ctx.clearRect(0, 0, ctx.canvas.width, ctx.canvas.height);
    stripsProgress.current = Array(NUM_STRIPS).fill(0);
    drawImage(ctx);
  };

  // Configurar canvas y buffers
  useEffect(() => {
    if (!image) return;

    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d', { alpha: true });
    const offCtx = offscreenCanvas.current.getContext('2d');

    const setupCanvas = () => {
      const container = canvas.parentElement;
      canvas.width = container.clientWidth * 2;
      canvas.height = container.clientHeight * 2;
      canvas.style.width = `${container.clientWidth}px`;
      canvas.style.height = `${container.clientHeight}px`;

      offscreenCanvas.current.width = canvas.width;
      offscreenCanvas.current.height = canvas.height;
    };

    setupCanvas();

    let animationFrameId;
    const animate = () => {
      // Suavizar transici칩n del efecto
      effectIntensity.current = effectIntensity.current.map((intensity, index) => {
        const target = hoveredStrip !== null && 
          Math.abs(index - currentStrip.current) <= EFFECT_RADIUS ? 
          1 - (Math.abs(index - currentStrip.current) * 0.25) : 0;
        return intensity + (target - intensity) * 0.15;
      });

      drawImage(ctx);
      animationFrameId = requestAnimationFrame(animate);
    };

    animate();

    const resizeObserver = new ResizeObserver(setupCanvas);
    resizeObserver.observe(canvas.parentElement);

    return () => {
      resizeObserver.disconnect();
      cancelAnimationFrame(animationFrameId);
    };
  }, [image, hoveredStrip]);

  const applyEffects = (ctx, index, intensity) => {
    if (intensity <= 0) return;

    const stripWidth = ctx.canvas.width / NUM_STRIPS;
    const centerX = index * stripWidth + stripWidth / 2;
    const centerY = ctx.canvas.height / 2;

    // Blur
    const blurAmount = intensity * 8;
    for(let i = 0; i < blurAmount; i++) {
      ctx.filter = `opacity(${0.3}) blur(${blurAmount}px)`;
      ctx.drawImage(
        offscreenCanvas.current,
        index * stripWidth, 0, stripWidth, ctx.canvas.height,
        index * stripWidth - blurAmount/2, 0,
        stripWidth + blurAmount, ctx.canvas.height
      );
    }

    // Transformaciones
    ctx.save();
    ctx.translate(centerX, centerY);
    ctx.rotate(-intensity * 0.03);
    ctx.scale(1 + intensity * 0.1, 1 + intensity * 0.05);
    ctx.translate(-centerX, -centerY);

    ctx.drawImage(
      offscreenCanvas.current,
      index * stripWidth, 0, stripWidth, ctx.canvas.height,
      index * stripWidth, 0, stripWidth, ctx.canvas.height
    );

    ctx.restore();
  };

  const drawImage = (ctx) => {
    if (!image || !ctx) return;

    const offCtx = offscreenCanvas.current.getContext('2d');

    // Dibujar en canvas offscreen
    offCtx.clearRect(0, 0, offscreenCanvas.current.width, offscreenCanvas.current.height);
    offCtx.drawImage(image, 0, 0, offscreenCanvas.current.width, offscreenCanvas.current.height);

    // Limpiar canvas principal
    ctx.clearRect(0, 0, ctx.canvas.width, ctx.canvas.height);

    // Dibujar strips con efectos
    const stripWidth = ctx.canvas.width / NUM_STRIPS;

    for(let index = 0; index < NUM_STRIPS; index++) {
      ctx.save();

      // M치scara de recorte
      ctx.beginPath();
      ctx.rect(
        index * stripWidth,
        0,
        stripWidth * stripsProgress.current[index],
        ctx.canvas.height
      );
      ctx.clip();

      // Aplicar efectos
      applyEffects(ctx, index, effectIntensity.current[index]);

      ctx.restore();
    }
  };

  const handleMouseMove = (e) => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const rect = canvas.getBoundingClientRect();
    const x = (e.clientX - rect.left) * (canvas.width / canvas.clientWidth);
    const stripIndex = Math.floor(x / (canvas.width / NUM_STRIPS));

    currentStrip.current = Math.min(Math.max(stripIndex, 0), NUM_STRIPS - 1);
    setHoveredStrip(currentStrip.current);
  };

  const startAnimation = () => {
    if (isAnimating.current) return;
    isAnimating.current = true;

    const animate = (startTime) => {
      const ctx = canvasRef.current?.getContext('2d');
      if (!ctx) return;

      stripsProgress.current = stripsProgress.current.map((_, index) => {
        const elapsed = performance.now() - startTime - (index * STRIP_DELAY);
        const rawProgress = Math.min(elapsed / ANIMATION_DURATION, 1);
        return 1 - Math.pow(1 - rawProgress, 3);
      });

      if (performance.now() - startTime < (NUM_STRIPS * STRIP_DELAY) + ANIMATION_DURATION) {
        animationRef.current = requestAnimationFrame(() => animate(startTime));
      } else {
        isAnimating.current = false;
        stripsProgress.current = Array(NUM_STRIPS).fill(1);
      }
    };

    animationRef.current = requestAnimationFrame(() => animate(performance.now()));
  };

  return (
    <div 
      className="home__carrousel" 
      onClick={startAnimation}
      onMouseMove={handleMouseMove}
      onMouseLeave={() => setHoveredStrip(null)}
      style={{ 
        position: 'relative',
        cursor: 'pointer',
        background: 'transparent',
        overflow: 'hidden'
      }}
    >
      <canvas 
        ref={canvasRef}
        style={{ 
          background: 'transparent',
          imageRendering: 'auto'
        }}
      />
    </div>
  );
};

export default HomeCarousel;
