// ./src/components/Home.js
import React, { useState, useEffect } from 'react';
import '../assets/App.css';
import Header from './Header';
// import Footer from './Footer'; // You can re-enable Footer if needed
import background from '../assets/background.mp4';
import GrainOverlay from './GrainOverlay';
import ClipLoader from 'react-spinners/ClipLoader';

const Home = () => {
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Simular un tiempo de carga del video
    const handleLoad = () => {
      setLoading(false);
    };

    // Asumiendo que el video tarda un tiempo en cargarse
    const videoElement = document.querySelector('.videoTag');
    if (videoElement) {
      videoElement.addEventListener('loadeddata', handleLoad);
    }

    // Limpiar el evento al desmontar el componente
    return () => {
      if (videoElement) {
        videoElement.removeEventListener('loadeddata', handleLoad);
      }
    };
  }, []);

  return (
    <div className="home">
      {loading ? (
        <div className="spinner-container">
          <ClipLoader color="#ffffff" loading={loading} size={150} />
          <p>Cargando la p√°gina web...</p>
        </div>
      ) : (
        <>
          <video className='videoTag' autoPlay loop muted>
            <source src={background} type='video/mp4' />
          </video>
          <GrainOverlay />
          <Header />
          <div className="content">
            <div className="maintenance">
              This webpage is in maintenance
            </div>
          </div>
          {/* <Footer /> */}
        </>
      )}
    </div>
  );
};

export default Home;
