import React from 'react';

const HomeCarousel = ({ section }) => {
  const sectionNumber = section + 1;
  console.log(`Cargando sección ${sectionNumber}`);

  const images = Array.from({ length: 20 }, (_, index) => {
    const imageNumber = index + 1;
    try {
      const img = require(`../assets/imgs/home_image_section${sectionNumber}/section${sectionNumber}_image${imageNumber}.jpg`);
      console.log('Objeto devuelto por require:', img);
      return img.default ? img.default : img;
    } catch (error) {
      console.error(`Error cargando imagen: section${sectionNumber}_image${imageNumber}.jpg`, error);
      return null;
    }
  }).filter(img => img !== null);

  console.log('Imágenes procesadas:', images);

  return (
    <div className="home__carrousel__img__rows">
      {images.map((img, index) => (
        <div
          key={index}
          className="image-part-container"
          style={{
            width: `${100 / images.length}%`,
            height: '100%',
            overflow: 'hidden',
            flexShrink: 0,
            backgroundColor: '#f00' // Borde temporal para debug
          }}
        >
          <img
            src={img}
            alt={`Sección ${sectionNumber} - Parte ${index + 1}`}
            style={{
              width: '100%',
              height: '100%',
              objectFit: 'cover',
              objectPosition: 'center',
              border: '2px solid blue'
            }}
            onError={(e) => {
              console.error('Error al cargar imagen:', e.target.src);
              e.target.style.backgroundColor = '#ff0';
            }}
          />
        </div>
      ))}
    </div>
  );
};

export default HomeCarousel;
