import { useRef } from "react";
import '../assets/styles/App.css';

const SpotlightBanner = ({ children, spotlightColor = "rgba(255, 255, 255, 0.05)" }) => {
  const bannerRef = useRef(null);

  const handleMouseMove = (e) => {
    const rect = bannerRef.current.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const y = e.clientY - rect.top;

    bannerRef.current.style.setProperty("--mouse-x", `${x}px`);
    bannerRef.current.style.setProperty("--mouse-y", `${y}px`);
    bannerRef.current.style.setProperty("--spotlight-color", spotlightColor);
  };

  return (
    <div
      ref={bannerRef}
      onMouseMove={handleMouseMove}
      className="banner__description"
    >
      {children}
    </div>
  );
};

export default SpotlightBanner;
