import React, { useState, useEffect } from 'react';
import Home from "./components/Home";
import About from "./components/About";
import Projects from "./components/Projects";
import Contact from "./components/Contact";
import Testing from "./components/Testing";
import Navbar from './components/Header';
import GrainOverlay from './components/GrainOverlay';
import Spinner from './components/Preloader'; // Importa el Spinner
import background from './assets/imgs/background.mp4';
import './assets/styles/App.css';

const App = () => {
  const [currentPage, setCurrentPage] = useState('home');
  const [showWelcome, setShowWelcome] = useState(true); // Controla la animaci칩n de bienvenida
  const [videoLoaded, setVideoLoaded] = useState(false); // Controla si el video de fondo est치 cargado
  const [overlayLoaded, setOverlayLoaded] = useState(false); // Estado para el overlay (si aplica)

  document.ontouchmove = function (event) {
    event.preventDefault();
  };

  useEffect(() => {
    const welcomeTimeout = setTimeout(() => {
      setShowWelcome(false);
    }, 5000);
    return () => clearTimeout(welcomeTimeout); // Limpiar timeout al desmontar
  }, []);

  const handleVideoLoad = () => {
    setVideoLoaded(true);
  };

  const handleOverlayLoad = () => {
    setOverlayLoaded(true);
  };

  const renderPage = () => {
    switch (currentPage) {
      case 'home':
        return <Home />;
      case 'about':
        return <About />;
      case 'projects':
        return <Projects />;
      case 'contact':
        return <Contact />;
      case 'testing':
        return <Testing />;
      default:
        return null;
    }
  };

  // Wait for both video and overlay to load before rendering the content
  if (!videoLoaded || !overlayLoaded) {
    return <Spinner />;
  }

  return (
    <>
      {/* Pantalla de bienvenida */}
      {showWelcome ? (
        <Spinner />
      ) : (
        <div>
          <GrainOverlay onLoad={handleOverlayLoad} /> {/* Use onLoad for GrainOverlay */}
          <Navbar setCurrentPage={setCurrentPage} />

          <div className='home'>
            <video
              className={`videoTag ${videoLoaded ? 'visible' : 'hidden'}`}
              autoPlay
              loop
              muted
              playsInline
              onLoadedData={handleVideoLoad}
            >
              <source src={background} type="video/mp4" />
            </video>
            {renderPage()}
          </div> 
        </div>
      )}
    </>
  );
};

export default App;
