import React, { useState, useEffect } from 'react';
import Home from "./components/Home";
import About from "./components/About";
import Projects from "./components/Projects";
import Contact from "./components/Contact";
import Testing from "./components/Testing";
import Navbar from './components/Header';
import Footer from './components/SectionFooter'; // Importa el Footer
import GrainOverlay from './components/GrainOverlay';
import background from './assets/imgs/background.mp4';
import './assets/styles/App.css';

const App = () => {
  const [currentPage, setCurrentPage] = useState('home');
  const [loading, setLoading] = useState(true); // Estado para el video
  const [initialLoad, setInitialLoad] = useState(true); // Estado para el preloader

  document.ontouchmove = function (event) {
    event.preventDefault();
  };

  useEffect(() => {
    const timer = setTimeout(() => {
      setInitialLoad(false); // Oculta la pantalla de carga tras 3 segundos
    }, 3000);

    const videoElement = document.querySelector('video');
    if (videoElement) {
      videoElement.onloadeddata = () => {
        setLoading(false); // Marca que el video está listo
      };

      const playPromise = videoElement.play();
      if (playPromise !== undefined) {
        playPromise.catch((error) => {
          console.log('Autoplay failed, requiring user interaction:', error);

          // Add a user interaction fallback
          const handleUserInteraction = () => {
            videoElement.play();
            document.removeEventListener('click', handleUserInteraction);
            document.removeEventListener('touchstart', handleUserInteraction);
          };

          document.addEventListener('click', handleUserInteraction);
          document.addEventListener('touchstart', handleUserInteraction);
        });
      }
    }

    return () => clearTimeout(timer); // Limpia el temporizador
  }, []);

  const renderPage = () => {
    switch (currentPage) {
      case 'home':
        return <Home />;
      case 'about':
        return <About />;
      case 'projects':
        return <Projects />;
      case 'contact':
        return <Contact />;
      case 'testing':
        return <Testing />;
      default:
        return null;
    }
  };

  return (
    <>
      {initialLoad ? ( // Pantalla de carga inicial
        <div className="loading-screen">
          <div className="spinner"></div>
        </div>
      ) : (
        <>
          <GrainOverlay />
          <Navbar setCurrentPage={setCurrentPage} />
          <div className='home'>
            <video className="videoTag" autoPlay loop muted playsInline>
              <source src={background} type="video/mp4" />
            </video>
            {!loading && renderPage()} {/* Renderiza el contenido solo cuando el video está listo */}
          </div>
        </>
      )}
    </>
  );
};

export default App;
