import React, { useState } from 'react';
import '../assets/styles/App.css';
import '../assets/styles/Nav.css';
import HomeCarousel from '../utils/HomeCarousel';

// SLIDER COMPONENT
const SectionSlider = ({ currentSection, goToSection, goToNext, goToPrev }) => {
  return (
    <div className='home__slider'>
      <div className='home__slider__prev' onClick={goToPrev}>PREV</div>
      <div className='home__slider__nav'>
        <div className='section__bar'>
          <ul className='section__links'>
            {[0, 1, 2].map((index) => (
              <li 
                key={index}
                className='onClick__Section__link' 
                onClick={() => goToSection(index)}
              >
                <div 
                  className={`dot cursor-in-transition ${currentSection === index ? 'dot__focused' : ''}`}
                ></div>
              </li>
            ))}
          </ul>
        </div>
      </div>
      <div className='home__slider__next' onClick={goToNext}>NEXT</div>
    </div>
  );
};

const About = () => {
  const [fadeActive, setFadeActive] = useState(false);
  const [currentSection, setCurrentSection] = useState(0);

  const totalImages = 20;
  const animationDuration = 600;
  const delayIncrement = 50;

  const handleImageClick = () => {
    setFadeActive(true);
    setTimeout(() => setFadeActive(false), totalImages * delayIncrement + animationDuration);
  };

  const goToSection = (index) => {
    setCurrentSection(index);
  };

  const goToNext = () => {
    setCurrentSection((prev) => (prev + 1) % 3);
  };

  const goToPrev = () => {
    setCurrentSection((prev) => (prev - 1 + 3) % 3);
  };

  return (
    <div>
      {/* SECCIÓN 0 */}
      {currentSection === 0 && (
        <section>
          <div>
            <div className='left__side__content'>
              <SectionSlider
                currentSection={currentSection}
                goToSection={goToSection}
                goToNext={goToNext}
                goToPrev={goToPrev}
              />
            </div>
            <div className='right__side__content'>
              <div className='home__tittle'>
                <div className='home__carrousel' onClick={handleImageClick}>
                  <HomeCarousel 
                    sectionIndex={0}
                    fadeActive={fadeActive}
                    animationDuration={animationDuration}
                    delayIncrement={delayIncrement}
                  />
                </div>
                <div className="home__subtittle">
                  {/* Mantenemos la estructura original del subtítulo */}
                </div>
              </div>
            </div>
          </div>
        </section>
      )}

      {/* SECCIÓN 1 */}
      {currentSection === 1 && (
        <section>
          <div>
            <div className='left__side__content'>
              <SectionSlider
                currentSection={currentSection}
                goToSection={goToSection}
                goToNext={goToNext}
                goToPrev={goToPrev}
              />
            </div>
            <div className='right__side__content'>
              <div className='home__tittle'>
                <div className='home__carrousel' onClick={handleImageClick}>
                  <HomeCarousel 
                    sectionIndex={1}
                    fadeActive={fadeActive}
                    animationDuration={animationDuration}
                    delayIncrement={delayIncrement}
                  />
                </div>
                <div className="home__subtittle">
                  {/* Mantenemos la estructura original del subtítulo */}
                </div>
              </div>
            </div>
          </div>
        </section>
      )}

      {/* SECCIÓN 2 */}
      {currentSection === 2 && (
        <section>
          <div>
            <div className='left__side__content'>
              <SectionSlider
                currentSection={currentSection}
                goToSection={goToSection}
                goToNext={goToNext}
                goToPrev={goToPrev}
              />
            </div>
            <div className='right__side__content'>
              <div className='home__tittle'>
                <div className='home__carrousel' onClick={handleImageClick}>
                  <HomeCarousel 
                    sectionIndex={2}
                    fadeActive={fadeActive}
                    animationDuration={animationDuration}
                    delayIncrement={delayIncrement}
                  />
                </div>
                <div className="home__subtittle">
                  {/* Mantenemos la estructura original del subtítulo */}
                </div>
              </div>
            </div>
          </div>
        </section>
      )}
    </div>
  );
};

export default About;
