import React, { useState, useEffect, useRef } from 'react';
import '../assets/styles/App.css';

const Cursor = () => {
  const [isHovering, setIsHovering] = useState(false);
  const cursorRef = useRef(null);

  const mouseX = useRef(0);
  const mouseY = useRef(0);
  const targetX = useRef(null);
  const targetY = useRef(null);
  const isAttracted = useRef(false);
  const animationFrameId = useRef(null);

  useEffect(() => {
    const moveCursor = (e) => {
      mouseX.current = e.clientX;
      mouseY.current = e.clientY;
    };

    const animate = () => {
      let newX = mouseX.current;
      let newY = mouseY.current;

      if (isAttracted.current && targetX.current !== null && targetY.current !== null) {
        const dx = mouseX.current - targetX.current;
        const dy = mouseY.current - targetY.current;
        const distance = Math.sqrt(dx * dx + dy * dy);
        const attractionThreshold = 100;

        if (distance < attractionThreshold) {
          const easing = 0.2;
          newX = mouseX.current + (targetX.current - mouseX.current) * easing;
          newY = mouseY.current + (targetY.current - mouseY.current) * easing;
        } else {
          isAttracted.current = false;
        }
      }

      if (cursorRef.current) {
        cursorRef.current.style.transform = `translate(${newX}px, ${newY}px)`;
        cursorRef.current.classList.toggle('attracted', isAttracted.current);
      }

      animationFrameId.current = requestAnimationFrame(animate);
    };

    animationFrameId.current = requestAnimationFrame(animate);
    window.addEventListener('mousemove', moveCursor);

    // Event listeners para elementos interactivos
    const interactiveElements = document.querySelectorAll(
      'a, button, .home-icon, .nav__toggler, .logo'
    );

    interactiveElements.forEach((element) => {
      element.addEventListener('mouseenter', () => setIsHovering(true));
      element.addEventListener('mouseleave', () => setIsHovering(false));
    });

    // Event listeners para los puntos del slider
    const interactiveLinks = document.querySelectorAll('.onClick__Section__link');
    interactiveLinks.forEach((link) => {
      link.addEventListener('mouseenter', () => {
        const dot = link.querySelector('.dot');
        if (dot) {
          const rect = dot.getBoundingClientRect();
          targetX.current = rect.left + rect.width / 2;
          targetY.current = rect.top + rect.height / 2;
          isAttracted.current = true;
        }
      });

      link.addEventListener('mouseleave', () => {
        isAttracted.current = false;
        targetX.current = null;
        targetY.current = null;
      });
    });

    return () => {
      window.removeEventListener('mousemove', moveCursor);
      cancelAnimationFrame(animationFrameId.current);
      interactiveElements.forEach((element) => {
        element.removeEventListener('mouseenter', () => setIsHovering(true));
        element.removeEventListener('mouseleave', () => setIsHovering(false));
      });
      interactiveLinks.forEach((link) => {
        link.removeEventListener('mouseenter', () => {});
        link.removeEventListener('mouseleave', () => {});
      });
    };
  }, []);

  return (
    <div
      ref={cursorRef}
      className={`cursor ${isHovering ? 'hovering' : ''}`}
    >
      <div className="cursor-inner"></div>
    </div>
  );
};

export default Cursor;
