import React from 'react';

const importSectionImages = (sectionNumber) => {
  const context = require.context(
    `../assets/imgs/home_image_section${sectionNumber + 1}`,
    false,
    /\.(jpg)$/
  );
  return context.keys().map(context);
};

const HomeCarousel = ({ sectionIndex, fadeActive, totalImages = 20, animationDuration = 600, delayIncrement = 50 }) => {
  const images = importSectionImages(sectionIndex);

  return (
    <div className='home__carrousel' style={{ position: 'relative' }}>
      <div className="home__carrousel__img__rows">
        {images.map((img, index) => (
          <div
            key={index}
            className="image-part-container"
            style={{
              animation: fadeActive 
                ? `expandWidth ${animationDuration}ms cubic-bezier(0.4, 0, 0.2, 1) forwards`
                : 'none',
              animationDelay: fadeActive ? `${index * delayIncrement}ms` : '0ms'
            }}
          >
            <img
              src={img.default}
              alt={`Sección ${sectionIndex + 1} - Parte ${index + 1}`}
              className="image-part"
            />
          </div>
        ))}
      </div>
    </div>
  );
};

export default HomeCarousel;
