import React, { useState } from "react";
import '../assets/styles/NavBar.css';

function Navbar({ setCurrentPage }) {
  const [active, setActive] = useState("nav__menu");
  const [icon, setIcon] = useState("nav__toggler");
  const [animateButton, setAnimateButton] = useState(false);

  const navToggle = () => {
    setActive(active === "nav__menu" ? "nav__menu nav__active" : "nav__menu");
    setIcon(icon === "nav__toggler" ? "nav__toggler toggle" : "nav__toggler");
  };

  const handleNavClick = (page) => {
    setCurrentPage(page);
    setActive("nav__menu");
    setIcon("nav__toggler");
  };

  const handleHomeClick = () => {
    setAnimateButton(true);
    setTimeout(() => {
      setAnimateButton(false);
      handleNavClick('home');
    }, 300);
  };

  return (
    <nav className="nav">
      <button
        type="button"
        className={`home__icon ${animateButton ? "animate" : ""}`} // Use animateButton state for animation
        onClick={handleHomeClick} // Change here to call the home click handler
        aria-label="Ir a Inicio"
      >
        <svg
          xmlns="http://www.w3.org/2000/svg"
          viewBox="0 0 300 300"
          shapeRendering="geometricPrecision"
          textRendering="geometricPrecision"
          className={animateButton ? "svg-shadow" : ""} // Add shadow class based on animation state
        >
          <path
            d="M81.5,55C63.18,62.28,57.28,68.18,50,86.5C42.72,68.18,36.82,62.28,18.5,55C36.82,47.72,42.72,41.82,50,23.5C57.28,41.82,63.18,47.72,81.5,55Z"
            transform="matrix(1.903076 0 0 1.903076 55.840411 44.501105)"
            fill="#5483cc"
          />
        </svg>
      </button>
      <ul className={active}>
        <li className="nav__item">
          <button className="nav__link" onClick={() => handleNavClick('about')}>
            About
          </button>
        </li>
        <li className="nav__item">
          <button className="nav__link" onClick={() => handleNavClick('projects')}>
            Projects
          </button>
        </li>
        <li className="nav__item">
          <button className="nav__link" onClick={() => handleNavClick('contact')}>
            Contact
          </button>
        </li>
      </ul>
      <div onClick={navToggle} className={icon}>
        <div className="line1"></div>
        <div className="line2"></div>
        <div className="line3"></div>
      </div>
    </nav>
  );
}

export default Navbar;
