import React, { useState, useEffect, useRef } from 'react';import '../assets/styles/App.css';

const Cursor = () => {
  const [position, setPosition] = useState({ x: 0, y: 0 });
  const [isHovering, setIsHovering] = useState(false);
  const cursorRef = useRef(null);

  useEffect(() => {
    const moveCursor = (e) => {
      const { clientX: x, clientY: y } = e;
      // Update the cursor position with a slight delay
      requestAnimationFrame(() => {
        setPosition({ x, y });
      });
    };

    const handleHover = () => {
      setIsHovering(true);
    };

    const handleLeave = () => {
      setIsHovering(false);
    };

    // Track mouse movement
    window.addEventListener('mousemove', moveCursor);

    // Track hover on all interactive elements
    const interactiveElements = document.querySelectorAll('a, button, .home-icon');
    interactiveElements.forEach((element) => {
      element.addEventListener('mouseenter', handleHover);
      element.addEventListener('mouseleave', handleLeave);
    });

    return () => {
      window.removeEventListener('mousemove', moveCursor);
      interactiveElements.forEach((element) => {
        element.removeEventListener('mouseenter', handleHover);
        element.removeEventListener('mouseleave', handleLeave);
      });
    };
  }, []);

  return (
    <div
      ref={cursorRef}
      className={`cursor ${isHovering ? 'hovering' : ''}`}
      style={{
        left: `${position.x}px`,
        top: `${position.y}px`,
      }}
    >
      <div className="cursor-inner"></div>
    </div>
  );
};

export default Cursor;
