import React, { useState, useEffect } from 'react';
import Home from "./components/Home";
import About from "./components/About";
import Projects from "./components/Projects";
import Contact from "./components/Contact";
import Testing from "./components/Testing";
import Navbar from './components/Header';
import Footer from './components/SectionFooter'; // Importa el Footer
import GrainOverlay from './components/GrainOverlay';
import background from './assets/imgs/background.mp4';
import './assets/styles/App.css';
import LoadingScreen from './components/LoadingScreen'; // Importa la pantalla de carga

const App = () => {
  const [currentPage, setCurrentPage] = useState('home');
  const [loading, setLoading] = useState(true);
  const [showWelcome, setShowWelcome] = useState(true);
  const [isPageLoading, setIsPageLoading] = useState(true); // Estado para la pantalla de carga inicial

  document.ontouchmove = function(event) {
    event.preventDefault();
  };

  useEffect(() => {
    // Simula la carga inicial con un temporizador de 3 segundos
    const timer = setTimeout(() => {
      setIsPageLoading(false); // Oculta la pantalla de carga
    }, 3000);

    return () => clearTimeout(timer); // Limpia el temporizador al desmontar el componente
  }, []);

  const renderPage = () => {
    switch (currentPage) {
      case 'home':
        return <Home loading={loading} showWelcome={showWelcome} />;
      case 'about':
        return <About />;
      case 'projects':
        return <Projects />;
      case 'contact':
        return <Contact />;
      case 'testing':
        return <Testing />;
      default:
        return null;
    }
  };

  return (
    <>
      {isPageLoading ? (
        <LoadingScreen />
      ) : (
        <>
          <GrainOverlay />
          <Navbar setCurrentPage={setCurrentPage} />
          <div className={`home ${loading ? 'loading' : 'loaded'}`}>
            <video className="videoTag" autoPlay loop muted playsInline>
              <source src={background} type="video/mp4" />
            </video>
            {renderPage()}
          </div>
        </>
      )}
    </>
  );
};

export default App;
