import React, { useState, useEffect } from 'react';
import section1Image from '../assets/imgs/carrousel_img_1.jpg';
import section2Image from '../assets/imgs/carrousel_img_2.jpg';
import section3Image from '../assets/imgs/carrousel_img_3.jpg';

const HomeCarousel = ({ section }) => {
  const [isExpanded, setIsExpanded] = useState(false);
  const [currentImage, setCurrentImage] = useState(section1Image);

  // Mapeo de secciones a imágenes
  const sectionImages = {
    0: section1Image,
    1: section2Image,
    2: section3Image
  };

  // Actualizar imagen cuando cambia la sección
  useEffect(() => {
    setCurrentImage(sectionImages[section]);
    setIsExpanded(false); // Resetear animación al cambiar sección
  }, [section]);

  // Manejar clic para animación
  const handleImageClick = () => {
    setIsExpanded(!isExpanded);
  };

  return (
    <div 
      className={`home__carrousel__container ${isExpanded ? 'expanded' : ''}`}
      onClick={handleImageClick}
    >
      <img
        src={currentImage}
        alt={`Sección ${section + 1}`}
        className={`home__carrousel__image ${isExpanded ? 'expanded' : ''}`}
      />
    </div>
  );
};

export default HomeCarousel;
