import React, { useState } from 'react';
import '../assets/styles/Nav.css';
import '../assets/styles/App.css';

const About = () => {
  return (
    <div className="about__container">
      <div className='about__content'>
        <h1>About Me</h1>
        <p>
          Hello! I'm John Lemon, a passionate software developer with a love for creating innovative solutions. With a background in computer science and years of experience in the industry, I specialize in building web applications that are both functional and user-friendly.
        </p>
        <p>
          In my free time, I enjoy exploring new technologies, contributing to open-source projects, and hiking in the great outdoors. I'm always eager to learn and take on new challenges.
        </p>
        <p>
          Feel free to reach out if you'd like to collaborate or just want to say hello!
        </p>
      </div>
    </div>
  );
};

export default About;
