import React, { useState, useEffect } from 'react';
import '../assets/styles/App.css';
import profile from '../assets/imgs/viper.jpg';
import SectionFooter from './SectionFooter'; // Importa el footer

const Home = ({ loading, showWelcome }) => {
  const [currentSection, setCurrentSection] = useState(1); // Maneja la sección activa

  useEffect(() => {
    document.body.style.overflow = 'hidden';
    document.body.style.touchAction = 'none';

    return () => {
      document.body.style.overflow = 'auto';
      document.body.style.touchAction = 'auto';
    };
  }, []);

  const renderSection = () => {
    switch (currentSection) {
      case 1:
        return <div className="section">

          <div className="profile">
            <img src={profile} alt="Profile" />
          </div>
          <div className="welcome-message">
            <p><b>hey</b></p>
            <p>i'm a developer based in <b>Barcelona</b>. I create things like&nbsp;
              <span style={{ color: '#e96f7c' }}>sites & apps</span> and delve into&nbsp;
              <span style={{ color: '#e96f7c' }}>hacking & malware</span> and whatever else.
              The page is still under maintenance, so feel free to look around if you want..
            </p>
          </div>

        </div>;


      case 2:
        return <div className="section">
          Section II Content
          </div>;


      case 3:
        return <div className="section">
          Section III Content
          </div>;


      default:
        return null;
    }
  };

  return (
    <div className="content">
      {!loading && showWelcome && (
        <div className="welcome">


          {renderSection()}

          <div style={{position:'absolute', bottom: '10px'}}>
            <SectionFooter currentSection={currentSection} setCurrentSection={setCurrentSection} />
          </div>

        </div>
      )}
    </div>
  );
};

export default Home;
