import React, { useRef, useEffect } from "react";
import * as THREE from "three";
import { OrbitControls } from "three/examples/jsm/controls/OrbitControls";

// CircleXZAnimation — 40 circunferencias ascendientes con gradiente continuo de colores pastel
// Gradiente vertical continuo (de abajo a arriba):
//  - Abajo: Rosa pastel (#ff8a8a)
//  - Centro abajo: Melocotón pastel (#ffc08a)
//  - Centro: Amarillo pastel (#ffff8a)
//  - Centro arriba: Verde menta pastel (#8affb3)
//  - Arriba: Azul cielo pastel (#8ac6ff)

export default function CircleXZAnimation() {
  const mountRef = useRef(null);

  useEffect(() => {
    const mount = mountRef.current;
    if (!mount) return;

    const scene = new THREE.Scene();
    scene.background = new THREE.Color(0x071026);

    const width = mount.clientWidth || window.innerWidth;
    const height = mount.clientHeight || window.innerHeight;

    const camera = new THREE.PerspectiveCamera(55, width / height, 0.1, 1000);
    camera.position.set(0, 140, 260);
    camera.lookAt(0, 50, 0);

    const renderer = new THREE.WebGLRenderer({ antialias: true });
    renderer.setSize(width, height);
    renderer.setPixelRatio(window.devicePixelRatio || 1);
    mount.appendChild(renderer.domElement);

    const amb = new THREE.AmbientLight(0xffffff, 0.55);
    scene.add(amb);
    const dir = new THREE.DirectionalLight(0xffffff, 0.4);
    dir.position.set(50, 120, 80);
    scene.add(dir);

    const grid = new THREE.GridHelper(300, 30, 0x333333, 0x1a1a1a);
    scene.add(grid);

    // Parámetros
    const R = 50; // radio de la "esfera"
    const Y_MIN = 0;
    const Y_MAX = 100;
    const segments = 256;
    const NUM_CIRCLES = 30; // 40 circunferencias

    const circles = [];

    // Colores del gradiente (de abajo hacia arriba) - colores pastel con menos saturación
    const colorRed = new THREE.Color(0xff8a8a);      // Rosa pastel - abajo
    const colorOrange = new THREE.Color(0xffc08a);   // Melocotón pastel
    const colorYellow = new THREE.Color(0xffff8a);   // Amarillo pastel
    const colorGreen = new THREE.Color(0x8affb3);    // Verde menta pastel
    const colorBlue = new THREE.Color(0x8ac6ff);     // Azul cielo pastel - arriba

    function lerp(a, b, t) {
      return a + (b - a) * t;
    }

    function blendColors(c1, c2, t) {
      return new THREE.Color(lerp(c1.r, c2.r, t), lerp(c1.g, c2.g, t), lerp(c1.b, c2.b, t));
    }

    function createCircle() {
      const positions = new Float32Array((segments + 1) * 3);
      const geometry = new THREE.BufferGeometry();
      geometry.setAttribute("position", new THREE.BufferAttribute(positions, 3));
      geometry.setDrawRange(0, segments + 1);

      const material = new THREE.LineBasicMaterial({
        color: colorOrange.clone(),
        transparent: true,
        opacity: 0.9,
      });

      const line = new THREE.Line(geometry, material);
      scene.add(line);

      return { line, geometry, positions, material };
    }

    function updateCircleGeometry(circle, radius) {
      const positions = circle.positions;
      for (let i = 0; i <= segments; i++) {
        const t = (i / segments) * Math.PI * 2;
        const x = radius * Math.cos(t);
        const z = radius * Math.sin(t);
        const idx = i * 3;
        positions[idx] = x;
        positions[idx + 1] = 0;
        positions[idx + 2] = z;
      }
      circle.geometry.attributes.position.needsUpdate = true;
    }

    function sphereRadiusAtY(yPos) {
      const val = R * R - (yPos - R) * (yPos - R);
      return val > 0 ? Math.sqrt(val) : 0;
    }

    // Crear círculos y asignar offs inicial
    const range = Y_MAX - Y_MIN;
    for (let i = 0; i < NUM_CIRCLES; i++) {
      const c = createCircle();
      c.initialOffset = (i / NUM_CIRCLES) * range;
      circles.push(c);
    }

    // Controles
    const controls = new OrbitControls(camera, renderer.domElement);
    controls.target.set(0, 50, 0);
    controls.update();

    // Tiempo absoluto para posiciones
    const startTime = performance.now();
    const speed = 6; // unidades Y por segundo

    let rafId = null;

    // Función para obtener color basado en posición Y con gradiente continuo
    function colorByGradient(yPos, radius) {
      // Normalizamos y a 0..1 (0 = abajo, 1 = arriba)
      const yNorm = (yPos - Y_MIN) / (Y_MAX - Y_MIN);

      // Definir puntos de transición del gradiente
      const gradientStops = [
        { pos: 0.0, color: colorRed },      // Abajo: Rosa pastel
        { pos: 0.25, color: colorOrange },  // Rosa-Melocotón
        { pos: 0.5, color: colorYellow },   // Melocotón-Amarillo
        { pos: 0.75, color: colorGreen },   // Amarillo-Verde menta
        { pos: 1.0, color: colorBlue }      // Arriba: Azul cielo
      ];

      // Encontrar entre qué dos stops está yNorm
      let startStop, endStop;
      for (let i = 0; i < gradientStops.length - 1; i++) {
        if (yNorm >= gradientStops[i].pos && yNorm <= gradientStops[i + 1].pos) {
          startStop = gradientStops[i];
          endStop = gradientStops[i + 1];
          break;
        }
      }

      // Si no encontramos stops (por si acaso), usar el primero
      if (!startStop || !endStop) {
        startStop = gradientStops[0];
        endStop = gradientStops[gradientStops.length - 1];
      }

      // Calcular factor de interpolación entre los dos colores
      const t = (yNorm - startStop.pos) / (endStop.pos - startStop.pos);
      let baseColor = blendColors(startStop.color, endStop.color, t);

      // Ajustar brillo según tamaño del círculo
      // Los círculos más grandes (cerca del centro) son ligeramente más brillantes
      const sizeFactor = radius / R;
      const brightness = 1.0 + 0.2 * sizeFactor; // Entre 1.0 y 1.2

      // Aplicar brillo suave
      baseColor.r = Math.min(baseColor.r * brightness, 1);
      baseColor.g = Math.min(baseColor.g * brightness, 1);
      baseColor.b = Math.min(baseColor.b * brightness, 1);

      // Reducir aún más la saturación para colores más pastel
      const desaturation = 1.25; // 70% de la saturación original
      const luminance = (baseColor.r + baseColor.g + baseColor.b) / 3;
      baseColor.r = luminance + (baseColor.r - luminance) * desaturation;
      baseColor.g = luminance + (baseColor.g - luminance) * desaturation;
      baseColor.b = luminance + (baseColor.b - luminance) * desaturation;

      // Sin contraste adicional para mantener colores suaves
      return baseColor;
    }

    function animate() {
      const now = performance.now();
      const elapsed = (now - startTime) / 1000; // segundos

      for (let i = 0; i < NUM_CIRCLES; i++) {
        const c = circles[i];
        const posInRange = (c.initialOffset + elapsed * speed) % range;
        const yPos = Y_MIN + posInRange;

        const radius = sphereRadiusAtY(yPos);
        updateCircleGeometry(c, radius);
        c.line.position.y = yPos;

        const color = colorByGradient(yPos, radius);
        c.material.color.copy(color);
      }

      renderer.render(scene, camera);
      rafId = requestAnimationFrame(animate);
    }

    rafId = requestAnimationFrame(animate);

    function handleResize() {
      const w = mount.clientWidth || window.innerWidth;
      const h = mount.clientHeight || window.innerHeight;
      camera.aspect = w / h;
      camera.updateProjectionMatrix();
      renderer.setSize(w, h);
    }

    window.addEventListener("resize", handleResize);

    // Limpieza al desmontar
    return () => {
      window.removeEventListener("resize", handleResize);
      if (rafId) cancelAnimationFrame(rafId);

      circles.forEach((c) => {
        scene.remove(c.line);
        c.geometry.dispose();
        c.material.dispose();
      });

      controls.dispose();
      renderer.dispose();
      if (renderer.domElement && mount.contains(renderer.domElement)) {
        mount.removeChild(renderer.domElement);
      }
    };
  }, []);

  return (
    <div ref={mountRef} style={{ width: "100vw", height: "100vh", position: "relative" }}>
      <div style={{ position: "absolute", left: 12, top: 12, padding: "6px 10px", background: "rgba(0,0,0,0.35)", color: "#e6fff0", borderRadius: 8, fontSize: 13 }}>
        40 circunferencias ascendientes — gradiente continuo de colores pastel suaves (rosa → melocotón → amarillo → verde menta → azul cielo)
      </div>
    </div>
  );
}
