import React, { useState, useEffect, useContext } from 'react';
import GrainOverlay from './components/GrainOverlay';
import Preloader from './components/Preloader';
import Home from './components/Home';
import About from './components/About';
import Projects from './components/Projects';
import Contact from './components/Contact';
import Cursor from './components/extra_functions/Cursor';
import Nav from './components/Nav';
import { ThemeContext } from './components/extra_functions/ThemeContext'; // Importa el contexto
import background from './assets/imgs/background.mp4';
import './assets/styles/App-dark-theme.css';
import './assets/styles/App-light-theme.css';

const App = () => {
  const [currentPage, setCurrentPage] = useState('home');
  const [videoLoaded, setVideoLoaded] = useState(false);
  const [timeoutCompleted, setTimeoutCompleted] = useState(false);
  const [isPreloaderVisible, setIsPreloaderVisible] = useState(true);
  const [isPreloaderClosing, setIsPreloaderClosing] = useState(false);

  // Obtén el tema del contexto
  const { theme } = useContext(ThemeContext);

  useEffect(() => {
    const welcomeTimeout = setTimeout(() => {
      setTimeoutCompleted(true);
    }, 5000);
    return () => clearTimeout(welcomeTimeout);
  }, []);

  useEffect(() => {
    if (timeoutCompleted && videoLoaded) {
      setIsPreloaderClosing(true);
      const fadeOut = setTimeout(() => {
        setIsPreloaderVisible(false);
      }, 1000);
      return () => clearTimeout(fadeOut);
    }
  }, [timeoutCompleted, videoLoaded]);

  const handleVideoLoad = () => {
    setVideoLoaded(true);
  };

  const renderPage = () => {
    switch (currentPage) {
      case 'home': return <Home setCurrentPage={setCurrentPage} />;
      case 'about': return <About />;
      case 'projects': return <Projects />;
      case 'contact': return <Contact />;
      default: return null;
    }
  };

  return (
    <>
      {isPreloaderVisible && <Preloader closing={isPreloaderClosing} />}

      {/* Usa la variable theme dinámica */}
      <div className={`app ${theme}`}>
        <GrainOverlay />
        <video
          className={`background-video ${videoLoaded ? 'visible' : 'hidden'}`}
          autoPlay
          loop
          muted
          playsInline
          onCanPlayThrough={handleVideoLoad}
        >
          <source src={background} type="video/mp4" />
        </video>
        <Nav setCurrentPage={setCurrentPage} />
        <div className="content">
          {renderPage()}
          <Cursor />
        </div>
      </div>
    </>
  );
};

export default App;
