import React from 'react';
import '../assets/styles/App.css';
import '../assets/styles/Nav.css';
import carrousel__img__1 from '../assets/imgs/carrousel__img__1.jpg';
import carrousel__img__2 from '../assets/imgs/carrousel__img__2.jpg';
import carrousel__img__3 from '../assets/imgs/carrousel__img__3.jpg';

const Home = () => {

  const handleMouseMove = (e) => {
    const carousel = e.currentTarget;
    const columns = carousel.querySelectorAll('.column');
    const rect = carousel.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const y = e.clientY - rect.top;

    columns.forEach((column, index) => {
      const columnRect = column.getBoundingClientRect();
      const columnCenter = columnRect.left + columnRect.width/2 - rect.left;
      const distance = Math.abs(x - columnCenter);
      const normalizedDistance = Math.min(distance / rect.width, 1);

      // Apply effects based on distance
      const blurAmount = (1 - normalizedDistance) * 8; // Max 8px blur
      const scaleAmount = 1 + (1 - normalizedDistance) * 0.2; // Up to 1.2x scale

      column.style.backdropFilter = `blur(${blurAmount}px)`;
      column.style.transform = `scaleY(${scaleAmount})`;
      column.style.setProperty('--distance', normalizedDistance);
    });
  };

  return (
    <div>


<div className='left__side__content'>
  <div className='banner__description'>
    <div className='banner__description__section'></div>
    <div className='banner__description__section'>
      My name is David Gomez Pilipenko aka //veqqar im a software developer and cybersecurity specialist. Based in Barcelona (ES).
    </div>
  </div>

  <div className='home__slider'>
    <button className='home__slidder__prev' onClick={''}>PREV</button>
    <div className='home__slidder__nav'> ■   ■   ■   ■   ■</div>
    <button className='home__slidder__next' onClick={''}>NEXT</button>
  </div>

  <div className='home__description_subtittle'>
    Me  
  </div>

  <div className='home__description_tittle'>
    About myself
  </div>

  <div className='description__link'>
    <button className='description__link__button'>
      Visit <i class="fa-solid fa-square-arrow-up-right"></i>
    </button>
  </div>
</div>



      <div className='right__side__content'>
        <div className='home__tittle'>
          <div className='header__section'>
            hi im <span className='home__name'>veqqar</span>
          </div>









          <div className='home__carrousel' onMouseMove={handleMouseMove}>
            <img 
              src={carrousel__img__1} 
              alt='Carousel' 
              className='home__carrousel__img' 
            />
            <div className='columns-overlay'>
              {[...Array(10)].map((_, i) => (
                <div 
                  key={i} 
                  className='column' 
                  data-distance={0}
                ></div>
              ))}
            </div>
          </div>







          <div className="home__subtittle">
            <div className="home__subtittle_sections">
              <div className="home__subtittle_section"></div>
              <div className="home__subtittle_section">
                <div className='home__subtittle_section__tittle'>
                  ■ Experience
                </div>
                <div className='home__subtittle_section__content' style={{fontSize: '1rem'}}>
                  // T+ 4y : 3m : 2w
                </div>
              </div>
              <div className="home__subtittle_section">
                <div className='home__subtittle_section__tittle'>
                  ■ Tools
                </div>
                <div className='home__subtittle_section__content'>
                  // C / Python / JS <br/>/ React / SQL / NoSQL <br/>/ Git / Docker / Kali <br/>/ Metasploit / Burp <br/>/ Nmap // 
                </div>
              </div>
              <div className="home__subtittle_section">
                <div className='home__subtittle_section__tittle'>
                  ■ Skills
                </div>
                <div className='home__subtittle_section__content'>
                  // Programming <br/> / Cyber-Security <br/> / Web-Dev / UI/UX //
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>

  );
};

export default Home;
