import React, { useState } from "react";
import '../assets/styles/NavBar.css';

function Navbar({ setCurrentPage }) {
  const [active, setActive] = useState("nav__menu");
  const [icon, setIcon] = useState("nav__toggler");
  const [animateButton, setAnimateButton] = useState(false); // Estado para manejar la animación de agrandarse
  const [shrink, setShrink] = useState(false); // Estado para manejar el shrink inicial
  const [fadeOut, setFadeOut] = useState(false); // Estado para manejar la animación de desaparición

  const navToggle = () => {
    setActive(active === "nav__menu" ? "nav__menu nav__active" : "nav__menu");
    setIcon(icon === "nav__toggler" ? "nav__toggler toggle" : "nav__toggler");
  };

  const handleNavClick = (page) => {
    setCurrentPage(page);
    setActive("nav__menu");
    setIcon("nav__toggler");
  };

  const handleHomeClick = () => {
    setShrink(true); // Comienza la animación de shrink
    setTimeout(() => {
      setShrink(false);
      setAnimateButton(true); // Habilita la animación de agrandamiento
      setTimeout(() => {
        setAnimateButton(false); // Finaliza la animación
        handleNavClick('home');
      }, 600); // Duración combinada de agrandamiento y sombra
    }, 300); // Duración de la animación de shrink
  };

  return (
    <nav className="nav">
      <button
        className={`home__icon ${animateButton ? 'grow' : ''}`}
        onClick={handleHomeClick}
      >
        <span className={`home-icon ${shrink ? 'shrink' : animateButton ? 'shadow' : ''}`}>✦</span>
      </button>
      <ul className={active}>
        <li className="nav__item">
          <button className="nav__link" onClick={() => handleNavClick('about')}>
            About
          </button>
        </li>
        <li className="nav__item">
          <button className="nav__link" onClick={() => handleNavClick('projects')}>
            Projects
          </button>
        </li>
        <li className="nav__item">
          <button className="nav__link" onClick={() => handleNavClick('contact')}>
            Contact
          </button>
        </li>
        <li className="nav__item">
          <button className="nav__link" onClick={() => handleNavClick('testing')}>
            Testing
          </button>
        </li>
      </ul>
      <div onClick={navToggle} className={icon}>
        <div className="line1"></div>
        <div className="line2"></div>
        <div className="line3"></div>
      </div>
    </nav>
  );
}

export default Navbar;
