const importAll = (context) => context.keys().map(context);

const imagesCache = {};

// En tu componente
const HomeCarousel = ({ section }) => {
  const sectionNumber = section + 1;

  if (!imagesCache[sectionNumber]) {
    try {
      imagesCache[sectionNumber] = importAll(
        require.context(
          `../assets/imgs/home_image_section${sectionNumber}`,
          false,
          /section\d+_image_\d+\.jpg$/
        )
      );
    } catch (error) {
      imagesCache[sectionNumber] = [];
    }
  }

  return (
    <div className="home__carrousel__img__rows">
      {imagesCache[sectionNumber].map((img, index) => (
        <div className="home__carrousel__img__rows">
        {images.map((img, index) => (
          <div
            key={index}
            className="image-part-container"
            style={{
              width: `${100 / images.length}%`,
              height: '100%',
              overflow: 'hidden',
              flexShrink: 0,
            }}
          >
            <img
              src={img}
              alt={`Sección ${sectionNumber} - Parte ${index + 1}`}
              className="images-rows"
            />
          </div>
        ))}
      </div>
      ))}
    </div>
  );
};

export default HomeCarousel;
