import React, { useState } from "react";
import '../assets/styles/NavBar.css';
import PropTypes from 'prop-types';

function Navbar({ setCurrentPage }) {
  const [active, setActive] = useState("nav__menu");
  const [icon, setIcon] = useState("nav__toggler");
  const [isAnimating, setIsAnimating] = useState(false); // Nuevo estado para la animación

  const navToggle = () => {
    setActive(active === "nav__menu" ? "nav__menu nav__active" : "nav__menu");
    setIcon(icon === "nav__toggler" ? "nav__toggler toggle" : "nav__toggler");
  };

  const handleNavClick = (page) => {
    setCurrentPage(page);
    setActive("nav__menu");
    setIcon("nav__toggler");

    // Iniciar la animación
    setIsAnimating(true);
    setTimeout(() => {
      setIsAnimating(false); // Restablecer la animación después de 600 ms
    }, 600);
  };

  return (
    <nav className="nav">
      <button
        type="button"
        className={`home__icon ${isAnimating ? "animate" : ""}`} // Agregar clase para animación
        onClick={() => handleNavClick('home')}
        aria-label="Ir a Inicio"
      >
        <svg
          xmlns="http://www.w3.org/2000/svg"
          viewBox="0 0 300 300"
          shapeRendering="geometricPrecision"
          textRendering="geometricPrecision"
          data-project-id="dcce1a0d054e4c97ab65319671264acd"
          data-export-id="91a91ab3104647a0a046e16ae132c516"
          data-cached="false"
        >
          <path
            d="M81.5,55C63.18,62.28,57.28,68.18,50,86.5C42.72,68.18,36.82,62.28,18.5,55C36.82,47.72,42.72,41.82,50,23.5C57.28,41.82,63.18,47.72,81.5,55Z"
            transform="matrix(1.903076 0 0 1.903076 55.840411 44.501105)"
            fill="#5483cc"
          />
        </svg>
      </button>
      <ul className={active}>
        <li className="nav__item" key="about">
          <button
            type="button"
            className="nav__link"
            onClick={() => handleNavClick('about')}
            aria-label="Ir a About"
          >
            About
          </button>
        </li>
        <li className="nav__item" key="projects">
          <button
            type="button"
            className="nav__link"
            onClick={() => handleNavClick('projects')}
            aria-label="Ir a Projects"
          >
            Projects
          </button>
        </li>
        <li className="nav__item" key="contact">
          <button
            type="button"
            className="nav__link"
            onClick={() => handleNavClick('contact')}
            aria-label="Ir a Contact"
          >
            Contact
          </button>
        </li>
      </ul>
      <div onClick={navToggle} className={icon} role="button" aria-label="Toggle Navigation" tabIndex="0">
        <div className="line1"></div>
        <div className="line2"></div>
        <div className="line3"></div>
      </div>
    </nav>
  );
}

Navbar.propTypes = {
  setCurrentPage: PropTypes.func.isRequired,
};

export default Navbar;
