import { useState } from 'react';

// Componente SectionSlider
const SectionSlider = ({ currentSection, goToSection, goToNext, goToPrev }) => {
  return (
    <div className='home__slider'>
      <button className='home__slidder__prev' onClick={goToPrev}>PREV</button>
      <div className='home__slidder__nav'>
        <div className='section__bar'>
          <ul className='section__links'>
            {[0, 1, 2].map((index) => (
              <li 
                key={index}
                className='onClick__Section__link' 
                onClick={() => goToSection(index)}
              >
                <div 
                  className={`dot cursor-in-transition ${currentSection === index ? 'active-dot' : ''}`}
                ></div>
              </li>
            ))}
          </ul>
        </div>
      </div>
      <button className='home__slidder__next' onClick={goToNext}>NEXT</button>
    </div>
  );
};

const Home = () => {
  const [currentSection, setCurrentSection] = useState(0);

  const goToSection = (index) => {
    setCurrentSection(index);
  };

  const goToNext = () => {
    setCurrentSection((prev) => (prev + 1) % 3);
  };

  const goToPrev = () => {
    setCurrentSection((prev) => (prev - 1 + 3) % 3);
  };

  return (
    <div>
      {/* Sección 0 */}
      {currentSection === 0 && (
        <section>
          {/* Tu contenido de la sección 0 aquí */}
          <h1>Sección 1</h1>
          <p>Contenido de la primera sección</p>

          <SectionSlider
            currentSection={currentSection}
            goToSection={goToSection}
            goToNext={goToNext}
            goToPrev={goToPrev}
          />
        </section>
      )}

      {/* Sección 1 */}
      {currentSection === 1 && (
        <section>
          {/* Tu contenido de la sección 1 aquí */}
          <h1>Sección 2</h1>
          <p>Contenido de la segunda sección</p>

          <SectionSlider
            currentSection={currentSection}
            goToSection={goToSection}
            goToNext={goToNext}
            goToPrev={goToPrev}
          />
        </section>
      )}

      {/* Sección 2 */}
      {currentSection === 2 && (
        <section>
          {/* Tu contenido de la sección 2 aquí */}
          <h1>Sección 3</h1>
          <p>Contenido de la tercera sección</p>

          <SectionSlider
            currentSection={currentSection}
            goToSection={goToSection}
            goToNext={goToNext}
            goToPrev={goToPrev}
          />
        </section>
      )}
    </div>
  );
};

export default Home;
