import React, { useState, useEffect } from 'react';
import '../assets/styles/App.css';

const Cursor = () => {
  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });
  const [cursorPosition, setCursorPosition] = useState({ x: 0, y: 0 });
  const [isHovering, setIsHovering] = useState(false);
  const [attractorPos, setAttractorPos] = useState(null);

  useEffect(() => {
    const moveCursor = (e) => {
      const { clientX: x, clientY: y } = e;
      setMousePosition({ x, y });
    };

      /* DISALLOW RIGHT CLICK */

      /*

    const handleContextMenu = (e) => {
      e.preventDefault();
    };

    document.addEventListener('contextmenu', handleContextMenu);

      */

    const handleHover = (e) => {
      setIsHovering(true);
      const dot = e.target.closest('.dot');
      if (dot) {
        const rect = dot.getBoundingClientRect();
        const centerX = rect.left + rect.width / 2;
        const centerY = rect.top + rect.height / 2;
        setAttractorPos({ x: centerX, y: centerY });
      }
    };

    const handleLeave = () => {
      setIsHovering(false);
      setAttractorPos(null);
    };

    window.addEventListener('mousemove', moveCursor);

    const dots = document.querySelectorAll('.dot');
    dots.forEach((dot) => {
      dot.addEventListener('mouseenter', handleHover);
      dot.addEventListener('mouseleave', handleLeave);
    });

    return () => {
      window.removeEventListener('mousemove', moveCursor);
      dots.forEach((dot) => {
        dot.removeEventListener('mouseenter', handleHover);
        dot.removeEventListener('mouseleave', handleLeave);
      });
    };
  }, []);

  useEffect(() => {
    let animationFrameId;
    const maxDistance = 150; // Radio reducido para puntos pequeños
    const attractionStrength = 0.4; // Fuerza aumentada

    const updateCursor = () => {
      if (attractorPos) {
        const dx = attractorPos.x - mousePosition.x;
        const dy = attractorPos.y - mousePosition.y;
        const distance = Math.sqrt(dx * dx + dy * dy);

        if (distance < maxDistance) {
          const ratio = (1 - distance / maxDistance) * attractionStrength;
          const newX = mousePosition.x + dx * ratio;
          const newY = mousePosition.y + dy * ratio;
          setCursorPosition({ x: newX, y: newY });
        } else {
          setCursorPosition(mousePosition);
        }
      } else {
        setCursorPosition(mousePosition);
      }
      animationFrameId = requestAnimationFrame(updateCursor);
    };

    animationFrameId = requestAnimationFrame(updateCursor);
    return () => cancelAnimationFrame(animationFrameId);
  }, [mousePosition, attractorPos]);

  return (
    <div
      className={`cursor ${isHovering ? 'hovering' : ''} ${attractorPos ? 'attracted' : ''}`}
      style={{
        left: `${cursorPosition.x}px`,
        top: `${cursorPosition.y}px`,
      }}
    >
      <div className="cursor-inner"></div>
    </div>
  );
};

export default Cursor;
