import React, { useEffect, useRef, useState } from 'react';
import '../assets/styles/Nav.css';
import '../assets/styles/App.css';
import DecryptedText from './extra_functions/DecryptedText';
import cv_qr from '../assets/imgs/qr-code-cv.svg';
import recycle_logo from '../assets/imgs/recycle_logo.svg';
import weee_symbol_logo from '../assets/imgs/WEEE_symbol_logo.svg';
import ce_logo from '../assets/imgs/CE_logo.svg';

const About = ({ setAboutSection, aboutSection }) => {
  const section1Ref = useRef(null);
  const section2Ref = useRef(null);
  const section3Ref = useRef(null);
  const section4Ref = useRef(null);

  const [isNavVisible, setIsNavVisible] = useState(true);
  const [mouseAttracted, setMouseAttracted] = useState(null);

  // Función para navegar a una sección específica
  const goToSection = (index) => {
    const sectionsContainer = document.querySelector('.about__sections__container');
    const windowHeight = window.innerHeight;
    sectionsContainer.scrollTo({
      top: index * windowHeight,
      behavior: 'smooth'
    });
  };

  useEffect(() => {
    const sectionsContainer = document.querySelector('.about__sections__container');
    const handleScroll = () => {
      const scrollPosition = sectionsContainer.scrollTop;
      const windowHeight = window.innerHeight;

      // Control de visibilidad del nav (solo visible en sección 1)
      if (scrollPosition < windowHeight * 0.5) {
        setAboutSection(0);
        setIsNavVisible(true);
      } else if (scrollPosition < windowHeight * 1.5) {
        setAboutSection(1);
        setIsNavVisible(false);
      } else if (scrollPosition < windowHeight * 2.5) {
        setAboutSection(2);
        setIsNavVisible(false);
      } else {
        setAboutSection(3);
        setIsNavVisible(false);
      }
    };

    sectionsContainer.addEventListener('scroll', handleScroll);
    return () => sectionsContainer.removeEventListener('scroll', handleScroll);
  }, [setAboutSection]);

  // Efecto de atracción del mouse (similar al home)
  useEffect(() => {
    const handleMouseMove = (e) => {
      if (mouseAttracted !== null) {
        const dotElement = document.querySelector(`.about__onClick__Section__link:nth-child(${mouseAttracted + 1}) .about__dot`);
        if (dotElement) {
          const rect = dotElement.getBoundingClientRect();
          const dotCenterX = rect.left + rect.width / 2;
          const dotCenterY = rect.top + rect.height / 2;

          const distance = Math.sqrt(
            Math.pow(e.clientX - dotCenterX, 2) + 
            Math.pow(e.clientY - dotCenterY, 2)
          );

          // Si el mouse está demasiado lejos, liberarlo
          if (distance > 100) {
            setMouseAttracted(null);
          }
        }
      }
    };

    window.addEventListener('mousemove', handleMouseMove);
    return () => window.removeEventListener('mousemove', handleMouseMove);
  }, [mouseAttracted]);

  const handleDotMouseEnter = (index) => {
    setMouseAttracted(index);
  };

  return (
    <div className="about__page__content">
      {/* Section Slider Vertical */}
      <div className={`about__slider ${isNavVisible ? 'about__slider--visible' : 'about__slider--hidden'}`}>
        <div className='about__slider__nav'>
          <div className='about__section__bar'>
            <ul className='about__section__links'>
              {[0, 1, 2, 3].map((index) => (
                <li 
                  key={index}
                  className={`about__onClick__Section__link ${mouseAttracted === index ? 'about__dot--attracted' : ''}`}
                  onClick={() => goToSection(index)}
                  onMouseEnter={() => handleDotMouseEnter(index)}
                  onMouseLeave={() => setMouseAttracted(null)}
                >
                  <div 
                    className={`about__dot cursor-in-transition ${aboutSection === index ? 'about__dot__focused' : ''}`}
                  ></div>
                </li>
              ))}
            </ul>
          </div>
        </div>
      </div>

      <div className="about__sections__container">

        {/* Sección 1 */}
        <section className="about__section1" ref={section1Ref}>
          <div className="about__left1">
            <h1 className="about__section1__title">
              <DecryptedText
                text="VEQQAR"
                encryptedText="V3QQ42"
                iterations={10}
                speed={35}
                charset="0123456789ABCDEFGHIJKLMNOPQRSTUVWXYZ!@#$%&"
                className="decrypted-text"
                encryptedClassName="encrypted-text"
                parentClassName="decrypted-text-container"
              />
            </h1>
          </div>
        </section>

        {/* Sección 2 */}
        <section className="about__section2" ref={section2Ref}>
          <div className="about__left2">
            <div className="about__section2__top">
              <div className="about__section2__whoami">
                <div className="about__section2__whoami__title">
                  1
                </div>
              </div>
              <div className="about__section2__personal">
                <div className="about__section2__personal__info">
                  2
                </div>
              </div>
              <div className='about__section2__hidden'>
                3
              </div>
            </div>

            <div className="about__section2__bottom">
              <div className="about__section2__barcode__container">
                <img 
                  src={cv_qr} 
                  className='about__section2__barcode'
                  alt=""
                />
              </div>

              <div className="about__section2__skills">
                <h3 className="about__section2__skills__title">Skills</h3>
                <ul className="about__section2__skills__list">
                  <li>Web Development</li>
                  <li>Cybersecurity</li>
                  <li>Python / JavaScript</li>
                  <li>React / Node.js</li>
                  <li>Penetration Testing</li>
                  <li>Cloud Infrastructure</li>
                </ul>
              </div>

              <div className="about__section2__links">
                <h3 className="about__section2__links__title">Connect</h3>
                <ul className="about__section2__links__list">
                  <li>
                    <a href="https://github.com/veqqar" target="_blank" rel="noopener noreferrer">
                      GitHub
                    </a>
                  </li>
                  <li>
                    <a href="https://linkedin.com/in/veqqar" target="_blank" rel="noopener noreferrer">
                      LinkedIn
                    </a>
                  </li>
                  <li>
                    <a href="mailto:veqqar@example.com">
                      Email
                    </a>
                  </li>
                  <li>
                    <a href="https://twitter.com/veqqar" target="_blank" rel="noopener noreferrer">
                      Twitter
                    </a>
                  </li>
                </ul>
              </div>
            </div>
          </div>

          <div className="about__right2">
          </div>
        </section>

        {/* Sección 3 */}
        <section className="about__section3" ref={section3Ref}>
          <div className="about__left3">
            <h1 className="about__section3__title">Philosophy</h1>
          </div>
          <div className="about__right3">
            <p className="about__section3__description">
              I believe in writing clean, maintainable code and following best practices in software development. 
              Continuous learning and adaptation are at the core of my professional philosophy.
              <br /><br />
              When I'm not coding, you can find me exploring new technologies, contributing to open-source projects, 
              or sharing knowledge with the developer community.
            </p>
          </div>
        </section>

        {/* Sección 4 - Nueva sección */}
        <section className="about__section4" ref={section4Ref}>
          <div className="about__left4">
            <h1 className="about__section4__title">Experience</h1>
          </div>
          <div className="about__right4">
            <p className="about__section4__description">
              With years of experience in both development and security, I've worked on various projects 
              ranging from web applications to security infrastructure. My expertise spans across multiple 
              technologies and domains.
            </p>
          </div>
        </section>

      </div>
    </div>
  );
};

export default About;
