import React, { useRef, useEffect } from "react";
import * as THREE from "three";

export default function Sphere() {
  const mountRef = useRef(null);

  useEffect(() => {
    const container = mountRef.current;
    if (!container) return;

    const scene = new THREE.Scene();

    const renderer = new THREE.WebGLRenderer({ antialias: true, alpha: true });
    renderer.setPixelRatio(window.devicePixelRatio || 1);
    container.appendChild(renderer.domElement);

    const camera = new THREE.PerspectiveCamera(50, 1, 0.1, 100);
    camera.position.z = 4;

    // Simple ambient-ish light (lines use their own color so lights aren't strictly necessary)
    const ambient = new THREE.AmbientLight(0xffffff, 0.2);
    scene.add(ambient);

    // Group for the static latitudinal rings (the 10 base rings)
    const baseGroup = new THREE.Group();
    scene.add(baseGroup);

    const R = 1.2; // conceptual sphere radius
    const ringSegments = 160;

    // Create a unit circle geometry lying on XZ plane, centered at origin (radius 1)
    function createUnitCircleGeometry(segments = ringSegments) {
      const positions = new Float32Array(segments * 3 + 3);
      for (let i = 0; i <= segments; i++) {
        const t = (i / segments) * Math.PI * 2;
        const x = Math.cos(t);
        const z = Math.sin(t);
        positions[i * 3 + 0] = x;
        positions[i * 3 + 1] = 0;
        positions[i * 3 + 2] = z;
      }
      const geometry = new THREE.BufferGeometry();
      geometry.setAttribute("position", new THREE.BufferAttribute(positions, 3));
      return geometry;
    }

    const unitCircle = createUnitCircleGeometry();

    // Neon materials: bright center line + softer enlarged line for glow
    function makeLineMaterials(hex = 0xff5577) {
      const bright = new THREE.LineBasicMaterial({
        color: hex,
        transparent: true,
        opacity: 1.0,
        depthWrite: false,
        blending: THREE.AdditiveBlending,
      });
      const glow = new THREE.LineBasicMaterial({
        color: hex,
        transparent: true,
        opacity: 0.18,
        depthWrite: false,
        blending: THREE.AdditiveBlending,
      });
      return { bright, glow };
    }

    const { bright: baseBrightMat, glow: baseGlowMat } = makeLineMaterials(0xff7b7b);

    // Build 10 base rings positioned as latitudes on a sphere
    const baseRings = [];
    const baseCount = 10;
    for (let i = 0; i < baseCount; i++) {
      // phi from -pi/2 .. pi/2
      const phi = (i / (baseCount - 1)) * Math.PI - Math.PI / 2;
      const y = Math.sin(phi) * R; // vertical position
      const ringRadius = Math.cos(phi) * R; // horizontal radius

      // bright line
      const geom = unitCircle.clone();
      const line = new THREE.Line(geom, baseBrightMat.clone());
      line.scale.set(ringRadius, 1, ringRadius);
      line.position.y = y;
      baseGroup.add(line);

      // faint glow line (slightly larger scaled version)
      const glowLine = new THREE.Line(geom.clone(), baseGlowMat.clone());
      glowLine.scale.set(ringRadius * 1.06, 1, ringRadius * 1.06);
      glowLine.position.y = y;
      baseGroup.add(glowLine);

      baseRings.push({ line, glowLine, y, ringRadius });
    }

    // Pulsing rings pool
    const pulses = [];
    const pulseMaterialSet = makeLineMaterials(0xff5f8b);

    function spawnPulse() {
      // Create a line from unit circle
      const geom = unitCircle.clone();
      const line = new THREE.Line(geom, pulseMaterialSet.bright.clone());
      const glow = new THREE.Line(geom.clone(), pulseMaterialSet.glow.clone());

      // Start slightly below the bottom of the sphere
      const startY = -R - 0.08;
      line.position.y = startY;
      glow.position.y = startY;

      // put both into group for easy rotation with baseGroup
      baseGroup.add(line);
      baseGroup.add(glow);

      // store pulse state
      pulses.push({ line, glow, y: startY, speed: 0.007 + Math.random() * 0.008, life: 0, fading: false });
    }

    // Spawn pulses periodically
    let lastSpawn = 0;
    const spawnInterval = 700; // ms

    // Animation loop
    let frameId;
    const clock = new THREE.Clock();

    function onResize() {
      const w = container.clientWidth;
      const h = container.clientHeight;
      renderer.setSize(w, h);
      camera.aspect = w / h;
      camera.updateProjectionMatrix();
    }

    onResize();
    window.addEventListener("resize", onResize);

    function animate() {
      const dt = clock.getDelta();
      const now = clock.elapsedTime * 1000;

      // rotate the whole group slowly to mimic the reference
      baseGroup.rotation.y += 0.25 * dt; // slow rotate
      baseGroup.rotation.x = Math.sin(clock.elapsedTime * 0.12) * 0.06;

      // update pulses
      for (let i = pulses.length - 1; i >= 0; i--) {
        const p = pulses[i];
        p.y += p.speed * (60 * dt * 60); // keep approximate speed independent of framerate
        p.line.position.y = p.y;
        p.glow.position.y = p.y;

        // scale the pulse ring horizontally to match a sphere cross-section of radius R
        const n = Math.max(-1, Math.min(1, p.y / R));
        const ringRadius = Math.sqrt(Math.max(0, 1 - n * n)) * R;
        const scale = Math.max(0.001, ringRadius);
        p.line.scale.set(scale, 1, scale);
        p.glow.scale.set(scale * 1.06, 1, scale * 1.06);

        p.life += dt * 1000;

        // Start fading when it reaches near top
        if (p.y > R - 0.18 && !p.fading) {
          p.fading = true;
        }

        if (p.fading) {
          // fade opacity gradually
          const fadeFactor = 1 - (p.y - (R - 0.18)) / (0.6 + Math.random() * 0.2);
          const clamped = Math.max(0, fadeFactor);
          p.line.material.opacity = clamped * 0.95;
          p.glow.material.opacity = clamped * 0.18;
        }

        // remove when beyond top
        if (p.y > R + 0.5) {
          baseGroup.remove(p.line);
          baseGroup.remove(p.glow);
          p.line.geometry.dispose();
          p.glow.geometry.dispose();
          p.line.material.dispose();
          p.glow.material.dispose();
          pulses.splice(i, 1);
        }
      }

      // occasionally spawn pulses
      if (now - lastSpawn > spawnInterval) {
        spawnPulse();
        lastSpawn = now;
      }

      renderer.render(scene, camera);
      frameId = requestAnimationFrame(animate);
    }

    // Add subtle CSS-based film grain overlay and background
    container.style.position = "relative";
    container.style.background = "radial-gradient(ellipse at center, #0b0c16 0%, #06050a 60%)";
    container.style.overflow = "hidden";

    // create a noise canvas overlay and append as child so it sits above the WebGL canvas
    const noiseCanvas = document.createElement("canvas");
    noiseCanvas.style.position = "absolute";
    noiseCanvas.style.top = "0";
    noiseCanvas.style.left = "0";
    noiseCanvas.style.width = "100%";
    noiseCanvas.style.height = "100%";
    noiseCanvas.style.pointerEvents = "none";
    noiseCanvas.style.mixBlendMode = "overlay";
    noiseCanvas.style.opacity = "0.07";
    container.appendChild(noiseCanvas);

    function resizeNoise() {
      noiseCanvas.width = container.clientWidth;
      noiseCanvas.height = container.clientHeight;
      const ctx = noiseCanvas.getContext("2d");
      const imgData = ctx.createImageData(noiseCanvas.width, noiseCanvas.height);
      for (let i = 0; i < imgData.data.length; i += 4) {
        const v = (Math.random() * 255) | 0;
        imgData.data[i] = v;
        imgData.data[i + 1] = v;
        imgData.data[i + 2] = v;
        imgData.data[i + 3] = 14; // low alpha
      }
      ctx.putImageData(imgData, 0, 0);
    }

    // initial resize & animate
    resizeNoise();
    onResize();
    animate();

    // expose renderer.domElement style so it fits container
    renderer.domElement.style.display = "block";
    renderer.domElement.style.width = "100%";
    renderer.domElement.style.height = "100%";

    // cleanup
    return () => {
      cancelAnimationFrame(frameId);
      window.removeEventListener("resize", onResize);
      container.removeChild(renderer.domElement);
      if (noiseCanvas.parentNode) container.removeChild(noiseCanvas);
      // dispose geometries & materials of base rings
      baseRings.forEach((b) => {
        if (b.line) {
          b.line.geometry.dispose();
          b.line.material.dispose();
        }
        if (b.glowLine) {
          b.glowLine.geometry.dispose();
          b.glowLine.material.dispose();
        }
      });
      pulses.forEach((p) => {
        if (p.line) {
          p.line.geometry.dispose();
          p.line.material.dispose();
        }
        if (p.glow) {
          p.glow.geometry.dispose();
          p.glow.material.dispose();
        }
      });
      renderer.dispose();
    };
  }, []);

  // container style: give it a default square size, but user can override with CSS
  return (
    <div
      ref={mountRef}
      style={{ width: "100%", height: "560px", maxWidth: "720px", margin: "0 auto", borderRadius: 8 }}
    />
  );
}
