import React, { useState, useEffect, useRef } from 'react';
import '../assets/styles/Nav.css';
import '../assets/styles/App.css';

const Projects = () => {
  const [activeIndex, setActiveIndex] = useState(0);
  const listRef = useRef(null);
  const itemRefs = useRef([]);
  const scrollTimeoutRef = useRef(null);

  const projects = [
    {
      title: "NexusFlow",
      info: "June 23 // Design UI & UX",
      infoFormatted: <>June <span className="project__info-numbers">23</span> // Design UI & UX</>
    },
    {
      title: "QuantumCore", 
      info: "April 22 // Software Dev",
      infoFormatted: <>April <span className="project__info-numbers">22</span> // Software Dev</>
    },
    {
      title: "ShadowNet",
      info: "March 24 // Malware Analysis", 
      infoFormatted: <>March <span className="project__info-numbers">24</span> // Malware Analysis</>
    },
    {
      title: "VulnScan Pro",
      info: "November 19 // Pentesting",
      infoFormatted: <>November <span className="project__info-numbers">19</span> // Pentesting</>
    },
    {
      title: "CroCoToolz",
      info: "September 25 // CyberSec Tools Dev",
      infoFormatted: <>September <span className="project__info-numbers">25</span> // CyberSec Tools Dev</>
    },
    {
      title: "MobiSecure",
      info: "January 23 // Mobile App Development",
      infoFormatted: <>January <span className="project__info-numbers">23</span> // Mobile App Development</>
    },
    {
      title: "CloudShield", 
      info: "July 22 // Cloud Infrastructure",
      infoFormatted: <>July <span className="project__info-numbers">22</span> // Cloud Infrastructure</>
    },
    {
      title: "DataCrypt",
      info: "August 23 // Encryption Systems",
      infoFormatted: <>August <span className="project__info-numbers">23</span> // Encryption Systems</>
    },
    {
      title: "NetWatch",
      info: "December 24 // Network Security",
      infoFormatted: <>December <span className="project__info-numbers">24</span> // Network Security</>
    },
    {
      title: "SecureComm",
      info: "May 23 // Secure Communications",
      infoFormatted: <>May <span className="project__info-numbers">23</span> // Secure Communications</>
    }
  ];

  // Centrar el primer item al cargar
  useEffect(() => {
    const timer = setTimeout(() => {
      if (itemRefs.current[0] && listRef.current) {
        centerItem(0);
      }
    }, 100); // Pequeño delay para asegurar que el DOM esté listo

    return () => clearTimeout(timer);
  }, []);

  // Detectar qué item está centrado durante el scroll - VERSIÓN MEJORADA
  const handleScroll = () => {
    if (!listRef.current) return;

    // Limpiar timeout anterior
    if (scrollTimeoutRef.current) {
      clearTimeout(scrollTimeoutRef.current);
    }

    // Usar timeout para mejorar rendimiento
    scrollTimeoutRef.current = setTimeout(() => {
      const list = listRef.current;
      const listRect = list.getBoundingClientRect();
      const listCenter = listRect.top + listRect.height / 2;

      let closestIndex = activeIndex;
      let closestDistance = Infinity;

      itemRefs.current.forEach((item, index) => {
        if (item) {
          const itemRect = item.getBoundingClientRect();
          const itemCenter = itemRect.top + itemRect.height / 2;
          const distance = Math.abs(itemCenter - listCenter);

          // Solo actualizar si está significativamente más cerca
          if (distance < closestDistance && distance < listRect.height * 0.4) {
            closestDistance = distance;
            closestIndex = index;
          }
        }
      });

      // Forzar la activación del último item si estamos cerca del final
      const scrollBottom = list.scrollHeight - list.scrollTop - list.clientHeight;
      if (scrollBottom < 50 && projects.length > 0) {
        closestIndex = projects.length - 1;
      }

      if (closestIndex !== activeIndex) {
        setActiveIndex(closestIndex);
      }
    }, 50);
  };

  // Función para centrar un item específico
  const centerItem = (index) => {
    if (itemRefs.current[index] && listRef.current) {
      const item = itemRefs.current[index];
      const list = listRef.current;

      // Calcular posición para centrar
      const itemTop = item.offsetTop;
      const itemHeight = item.offsetHeight;
      const listHeight = list.clientHeight;

      const targetScroll = itemTop - (listHeight / 2) + (itemHeight / 2);

      list.scrollTo({
        top: targetScroll,
        behavior: 'smooth'
      });

      setActiveIndex(index);
    }
  };

  // Cleanup timeout al desmontar
  useEffect(() => {
    return () => {
      if (scrollTimeoutRef.current) {
        clearTimeout(scrollTimeoutRef.current);
      }
    };
  }, []);

  return (
    <div className="projects__container">
      <div 
        className='projects__list' 
        ref={listRef}
        onScroll={handleScroll}
      >
        {projects.map((project, index) => (
          <div 
            key={index} 
            className={`project__item ${activeIndex === index ? 'active' : ''}`}
            ref={el => itemRefs.current[index] = el}
            onClick={() => centerItem(index)}
          >
            <div className='project__title'>{project.title}</div>
            <div className='project__info'>{project.infoFormatted}</div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Projects;
