// AdditionalStuff.jsx
import { useEffect } from 'react';
import '../assets/styles/App.css';

const useDisableContextMenu = () => {
    useEffect(() => {
      const handleContextMenu = (e) => {
        e.preventDefault();
        const rightClickMessage = document.createElement('div');
        rightClickMessage.innerText = 'not possible';
        rightClickMessage.className = 'right-click-message';
        rightClickMessage.style.top = `${e.clientY}px`;
        rightClickMessage.style.left = `${e.clientX}px`;
        document.body.appendChild(rightClickMessage);

        const handleClick = () => {
          if (document.body.contains(rightClickMessage)) {
            document.body.removeChild(rightClickMessage);
          }
          document.removeEventListener('click', handleClick);
        };

        document.addEventListener('click', handleClick);
        document.addEventListener('contextmenu', handleClick);
      };

      window.onload = function() {
        document.addEventListener("contextmenu", handleContextMenu, false);
      };

      return () => {
        document.removeEventListener("contextmenu", handleContextMenu);
      };
    }, []);
  };

  export default useDisableContextMenu;
