import '../assets/styles/App.css';
import profile from '../assets/imgs/viper.jpg';

const About = () => {
  return (
    <div className="about-container">
      <div className="about-text">
        <p>
          Hi, I'm a passionate developer from <b>Barcelona, Spain</b>. With a deep interest in
          technology, I enjoy building websites, applications, and exploring the world of cybersecurity.
          My focus spans across web development, hacking, and malware analysis, where I combine both
          creativity and logic. Over the years, Iâ€™ve worked on numerous projects that have helped
          me grow both personally and professionally.
        </p>
        <ul className="about-details">
          <li>Location: Barcelona, Spain</li>
          <li>Expertise: Web Development, Cybersecurity</li>
          <li>Hobbies: Hacking, Malware Analysis</li>
          <li>Experience: 5+ Years</li>
        </ul>
      </div>
      <div className="about-image">
        <img src={profile} alt="Profile" />
      </div>
    </div>
  );
};

export default About;
