import React, { useEffect } from 'react';
import '../assets/styles/App.css';

const SectionTransition = ({ origin, destination, direction, isAnimating, children }) => {
  useEffect(() => {
    if (!isAnimating) return;

    const timeout = setTimeout(() => {
      // Desactivar la animación después de un tiempo (según la duración de la animación CSS)
    }, 500);

    return () => clearTimeout(timeout);
  }, [isAnimating]);

  return (
    <div
      className={`section-transition ${direction === 'down' ? 'slide-down' : 'slide-up'} ${isAnimating ? 'animating' : ''}`}
    >
      {children}
    </div>
  );
};

export default SectionTransition;
