import React from 'react';

const SectionFooter = ({ currentSection, setCurrentSection }) => {
  return (
    <div
      style={{
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        color: "white",
        gap: "10rem",
        marginTop: "50px",
      }}
    >
      <ul
        style={{
          display: 'flex',
          listStyleType: 'none',
          padding: 0,
          gap: '10rem',
          fontSize: '3px',
        }}
      >
        {/* Primer li con el efecto de destello */}
        <li
          onClick={() => setCurrentSection(1)}
          style={{
            width: '50px', // Tamaño de la caja
            height: '50px',
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
            borderRadius: '50%', // Para que tenga forma circular
            border: '2px solid #fff', // Borde para mejor visibilidad
            transition: 'all 0.3s ease', // Transición suave
            cursor: 'pointer', // Indicador de que es seleccionable
          }}
        >
          <div
            className={currentSection === 1 ? 'dot__focused' : 'dot'}
            style={{
              width: '20px', // Tamaño del dot
              height: '20px',
              backgroundColor: '#fff', // Color del dot
              borderRadius: '50%', // Forma circular
              transition: 'transform 0.3s ease', // Transición para efecto hover
            }}
          />
        </li>

        <li
          onClick={() => setCurrentSection(2)}
          style={{
            width: '50px', // Tamaño de la caja
            height: '50px',
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
            borderRadius: '50%',
            border: '2px solid #fff',
            transition: 'all 0.3s ease',
            cursor: 'pointer',
          }}
        >
          <div
            className={currentSection === 2 ? 'dot__focused' : 'dot'}
            style={{
              width: '20px',
              height: '20px',
              backgroundColor: '#fff',
              borderRadius: '50%',
              transition: 'transform 0.3s ease',
            }}
          />
        </li>

        <li
          onClick={() => setCurrentSection(3)}
          style={{
            width: '50px', // Tamaño de la caja
            height: '50px',
            display: 'flex',
            justifyContent: 'center',
            alignItems: 'center',
            borderRadius: '50%',
            border: '2px solid #fff',
            transition: 'all 0.3s ease',
            cursor: 'pointer',
          }}
        >
          <div
            className={currentSection === 3 ? 'dot__focused' : 'dot'}
            style={{
              width: '20px',
              height: '20px',
              backgroundColor: '#fff',
              borderRadius: '50%',
              transition: 'transform 0.3s ease',
            }}
          />
        </li>
      </ul>
    </div>
  );
};

export default SectionFooter;
