// Home.js
import React, { useState, useEffect } from 'react';
import '../../assets/styles/App.css';
import '../../assets/styles/Nav.css';
import SectionAbout from './SectionAbout';
import SectionProjects from './SectionProjects';
import SectionContact from './SectionContact';

const Home = ({ setCurrentPage }) => {
  const [hoveredRow, setHoveredRow] = useState(null);
  const [fadeActive, setFadeActive] = useState(false);
  const totalRows = 20;
  const animationDuration = 600;
  const delayIncrement = 50;
  const [currentSection, setCurrentSection] = useState(0);
  const [timeSince, setTimeSince] = useState('');

  // Funci칩n compartida para el efecto de click
  const handleImageClick = () => {
    setFadeActive(true);
    setTimeout(() => setFadeActive(false), totalRows * delayIncrement + animationDuration);
  };

  // C치lculo de experiencia (mantenido en el componente principal)
  useEffect(() => {
    const calculateTimeSince = () => {
      const specificDate = new Date('2021-10-25');
      const currentDate = new Date();
      const differenceInMs = currentDate - specificDate;
      const msInYear = 1000 * 60 * 60 * 24 * 365.25;
      const msInMonth = 1000 * 60 * 60 * 24 * 30.44;
      const msInWeek = 1000 * 60 * 60 * 24 * 7;

      const years = Math.floor(differenceInMs / msInYear);
      const remainingMsAfterYears = differenceInMs % msInYear;
      const months = Math.floor(remainingMsAfterYears / msInMonth);
      const remainingMsAfterMonths = remainingMsAfterYears % msInMonth;
      const weeks = Math.floor(remainingMsAfterMonths / msInWeek);

      setTimeSince(`T+ ${years}y : ${months}m : ${weeks}w`);
    };
    calculateTimeSince();
  }, []);

  // Control de secciones
  const goToSection = (index) => setCurrentSection(index);
  const goToNext = () => setCurrentSection((prev) => (prev + 1) % 3);
  const goToPrev = () => setCurrentSection((prev) => (prev - 1 + 3) % 3);

  return (
    <div>
      {currentSection === 0 && (
        <SectionAbout
          setCurrentPage={setCurrentPage}
          currentSection={currentSection}
          goToSection={goToSection}
          goToNext={goToNext}
          goToPrev={goToPrev}
          timeSince={timeSince}
          hoveredRow={hoveredRow}
          setHoveredRow={setHoveredRow}
          fadeActive={fadeActive}
          handleImageClick={handleImageClick}
          animationDuration={animationDuration}
          delayIncrement={delayIncrement}
          totalRows={totalRows}
        />
      )}

      {currentSection === 1 && (
        <SectionProjects
          setCurrentPage={setCurrentPage}
          currentSection={currentSection}
          goToSection={goToSection}
          goToNext={goToNext}
          goToPrev={goToPrev}
          timeSince={timeSince}
          hoveredRow={hoveredRow}
          setHoveredRow={setHoveredRow}
          fadeActive={fadeActive}
          handleImageClick={handleImageClick}
          animationDuration={animationDuration}
          delayIncrement={delayIncrement}
          totalRows={totalRows}
        />
      )}

      {currentSection === 2 && (
        <SectionContact
          setCurrentPage={setCurrentPage}
          currentSection={currentSection}
          goToSection={goToSection}
          goToNext={goToNext}
          goToPrev={goToPrev}
          timeSince={timeSince}
          hoveredRow={hoveredRow}
          setHoveredRow={setHoveredRow}
          fadeActive={fadeActive}
          handleImageClick={handleImageClick}
          animationDuration={animationDuration}
          delayIncrement={delayIncrement}
          totalRows={totalRows}
        />
      )}
    </div>
  );
};

export default Home;
