import React, { useRef, useEffect } from "react";
import * as THREE from "three";

// Componente React que crea una circunferencia en el plano X (horizontal) - Z (profundidad)
// y la anima en altura desde y=0 hasta y=100. El radio sigue esta curva:
//  - y=0 -> radio = 0
//  - y=50 -> radio = 50
//  - y=100 -> radio = 0
// El movimiento hace "ping-pong" entre 0 y 100 (sube y baja continuamente).

export default function CircleXZAnimation() {
  const mountRef = useRef(null);

  useEffect(() => {
    const mount = mountRef.current;
    if (!mount) return;

    // Escena, cámara y renderer
    const scene = new THREE.Scene();
    scene.background = new THREE.Color(0x0b1020);

    const width = mount.clientWidth;
    const height = mount.clientHeight;

    const camera = new THREE.PerspectiveCamera(60, width / height, 0.1, 1000);
    camera.position.set(0, 120, 200);
    camera.lookAt(0, 50, 0);

    const renderer = new THREE.WebGLRenderer({ antialias: true });
    renderer.setSize(width, height);
    renderer.setPixelRatio(window.devicePixelRatio);
    mount.appendChild(renderer.domElement);

    // Luces sutiles
    const ambient = new THREE.AmbientLight(0xffffff, 0.6);
    scene.add(ambient);
    const dir = new THREE.DirectionalLight(0xffffff, 0.6);
    dir.position.set(50, 100, 50);
    scene.add(dir);

    // Ayuda visual: un grid en XZ
    const grid = new THREE.GridHelper(300, 30, 0x444444, 0x222222);
    scene.add(grid);

    // Geometría base: un anillo muy fino de radio 1 (usaremos escala para alcanzar el radio deseado)
    const inner = 0.98; // pequeño grosor para que se vea como contorno fino
    const outer = 1.0;
    const segments = 128;
    const ringGeo = new THREE.RingGeometry(inner, outer, segments);
    // Material visible por ambos lados
    const ringMat = new THREE.MeshBasicMaterial({
      color: 0x7be495,
      side: THREE.DoubleSide,
      transparent: true,
      opacity: 0.95,
    });

    const ring = new THREE.Mesh(ringGeo, ringMat);
    // Por defecto la geometría está en el plano XY; la rotamos al plano XZ (normal = Y)
    ring.rotation.x = -Math.PI / 2;
    scene.add(ring);

    // Variables de animación
    let y = 0; // altura actual
    let dirUp = 1; // 1 subiendo, -1 bajando
    const speed = 20; // unidades por segundo (ajustable)
    const baseRadius = 1; // geometría de referencia

    // Función que mapea y->[radio]
    function radiusFromY(yPos) {
      if (yPos <= 50) return (yPos / 50) * 50; // va de 0 a 50
      return ((100 - yPos) / 50) * 50; // va de 50 a 0
    }

    // Reloj para manejar delta time
    const clock = new THREE.Clock();

    function animate() {
      const delta = clock.getDelta();

      // Actualizamos la altura
      y += dirUp * speed * delta;
      if (y >= 100) {
        y = 100;
        dirUp = -1;
      } else if (y <= 0) {
        y = 0;
        dirUp = 1;
      }

      // Calculamos radio deseado
      const r = radiusFromY(y);
      const scale = r / baseRadius; // baseRadius es 1, pero dejamos por claridad

      // Aplicamos la escala en XZ (la geometría está rotada, así que escala uniforme está bien)
      ring.scale.set(scale, scale, scale);

      // Posicionamos la circunferencia en la altura actual
      ring.position.y = y;

      renderer.render(scene, camera);
      requestAnimationFrame(animate);
    }

    // Empezamos animación
    clock.start();
    animate();

    // Resize handler
    function handleResize() {
      const w = mount.clientWidth;
      const h = mount.clientHeight;
      camera.aspect = w / h;
      camera.updateProjectionMatrix();
      renderer.setSize(w, h);
    }

    window.addEventListener("resize", handleResize);

    // Cleanup
    return () => {
      window.removeEventListener("resize", handleResize);
      mount.removeChild(renderer.domElement);
      // Dispose geometries y materiales
      ring.geometry.dispose();
      ring.material.dispose();
      renderer.dispose();
    };
  }, []);

  return (
    <div className="w-full h-screen" ref={mountRef}>
      {/* El canvas de three.js se inyecta dentro de este div */}
      <div className="absolute top-4 left-4 text-sm text-white/90 bg-black/30 p-2 rounded">Circunferencia en XZ animada (y: 0 → 100)</div>
    </div>
  );
}
