import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion'; // Importar Framer Motion

const Home = () => {
  const [currentSection, setCurrentSection] = useState(1);

  const handleScroll = (e) => {
    if (e.deltaY > 0 && currentSection < 3) {
      setCurrentSection((prev) => prev + 1);
    } else if (e.deltaY < 0 && currentSection > 1) {
      setCurrentSection((prev) => prev - 1);
    }
  };

  useEffect(() => {
    window.addEventListener('wheel', handleScroll);
    return () => window.removeEventListener('wheel', handleScroll);
  }, [currentSection]);

  const renderSection = () => {
    switch (currentSection) {
      case 1:
        return (
          <motion.div
            className="home__section1"
            initial={{ opacity: 0, y: -100 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: 100 }} // Desaparece con un movimiento hacia abajo
            transition={{ duration: 0.5 }}
          >
            <h1>Section 1</h1>
          </motion.div>
        );

      case 2:
        return (
          <motion.div
            className="home__section2"
            initial={{ opacity: 0, y: 100 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: -100 }} // Desaparece con un movimiento hacia arriba
            transition={{ duration: 0.5 }}
          >
            <h1>Section 2</h1>
          </motion.div>
        );

      case 3:
        return (
          <motion.div
            className="home__section3"
            initial={{ opacity: 0, y: 100 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: -100 }} // Desaparece con un movimiento hacia arriba
            transition={{ duration: 0.5 }}
          >
            <h1>Section 3</h1>
          </motion.div>
        );

      default:
        return null;
    }
  };

  return <div>{renderSection()}</div>;
};

export default Home;
