const About = () => {
  const [currentSection, setCurrentSection] = useState(0);

  const goToSection = (index) => setCurrentSection(index);
  const goToNext = () => setCurrentSection(prev => (prev + 1) % 3);
  const goToPrev = () => setCurrentSection(prev => (prev - 1 + 3) % 3);

  return (
    <div>
      <SectionSlider
        currentSection={currentSection}
        goToSection={goToSection}
        goToNext={goToNext}
        goToPrev={goToPrev}
      />

      {[0, 1, 2].map((sectionIndex) => (
        currentSection === sectionIndex && (
          <section key={sectionIndex}>
            <div className='right__side__content'>
              <div className='home__title'>  {/* Corregido typo */}
                <div className='home__carrousel'>
                  <HomeCarousel section={sectionIndex} />
                </div>
              </div>
            </div>
          </section>
        )
      ))}
    </div>
  );
};

export default About;
