import React from 'react';
import '../assets/styles/Nav.css';
import '../assets/styles/App.css';

const Projects = () => {
  const projects = [
    {
      title: "Project 1",
      info: "June 23 // Design UI & UX",
      description: "A comprehensive UI/UX design project focusing on user experience and modern design principles."
    },
    {
      title: "Project 2", 
      info: "April 22 // Software Dev",
      description: "Full-stack web application development with React and Node.js implementing modern architecture."
    },
    {
      title: "Project 3",
      info: "March 24 // Malware Dev", 
      description: "Research and development in cybersecurity focusing on malware analysis and prevention techniques."
    },
    {
      title: "Project 4",
      info: "November 19 // Pentesting",
      description: "Penetration testing and security assessment for enterprise-level infrastructure and applications."
    },
    {
      title: "Project 5",
      info: "September 25 // CyberSec Tools Dev",
      description: "Development of custom cybersecurity tools and automation scripts for security operations."
    },
    {
      title: "Project 6",
      info: "January 23 // Mobile App Development",
      description: "Cross-platform mobile application development with React Native and Firebase integration."
    },
    {
      title: "Project 7", 
      info: "July 22 // Cloud Infrastructure",
      description: "AWS cloud infrastructure setup and DevOps implementation for scalable web applications."
    }
  ];

  return (
    <div className="projects__container">
      <div className='projects__list'>
        {projects.map((project, index) => (
          <div key={index} className='project__item'>
            <div className='project__title'>{project.title}</div>
            <div className='project__info'>{project.info}</div>
            {/* Opcional: añadir descripción si quieres más detalle */}
            {/* <div className='project__description'>{project.description}</div> */}
          </div>
        ))}
      </div>
    </div>
  );
};

export default Projects;
