import React, { useState } from 'react';
import '../assets/styles/App.css';
import profile1 from '../assets/imgs/dot-man.gif';

const About = () => {
  // Estado para controlar la sección actual
  const [activeSection, setActiveSection] = useState(1);

  // Renderizado condicional basado en la sección activa
  const renderSection = () => {
    switch (activeSection) {
      case 1:
        return (
          <div className="about-1">
            <div className="about-text">
              <div className="about-bio">
                <p>I’ve studied cybersecurity and am currently diving into computer science software engineering. I’ve dabbled in a lot, like  
                  pentesting, OSINT, malware dev, software dev and websites. I just kind of do it all. 
                  Nothing too crazy, but there's always more to learn, though.</p>
              </div>
              <div className="about-bio-list">
                <ul>
                  <li>Location: Barcelona, Spain</li>
                  <li>Expertise: Software Development & Cybersecurity</li>
                  <li>Hobbies: Cook</li>
                  <li>Experience: <span style={{ fontFamily: 'arial', fontSize: '15px' }}>5</span>+ Years</li>
                </ul>
              </div>
            </div>
            <div className="about-bio-img">
              <img src={profile1} alt="Profile" />
            </div>
            <div className="scroll-button">
              <button onClick={() => setActiveSection(2)}>Go to Section 2</button>
            </div>
          </div>
        );
      case 2:
        return (
          <div>
            <p>This is the content of the second section!</p>
            <div className="scroll-button">
              <button onClick={() => setActiveSection(3)}>Go to Section 3</button>
            </div>
            <div className="back-button">
              <button onClick={() => setActiveSection(1)}>Back to Section 1</button>
            </div>
          </div>
        );
      case 3:
        return (
          <div>
            <p>This is the content of the third section!</p>
            <div className="back-button">
              <button onClick={() => setActiveSection(2)}>Back to Section 2</button>
            </div>
          </div>
        );
      default:
        return null;
    }
  };

  return (
    <div className="content">
      {renderSection()} {/* Sección renderizada condicionalmente */}
    </div>
  );
};

export default About;
